import React, {useEffect, useState} from 'react'
import styled from 'styled-components'
import ImgSlider from './ImgSlider'
import Viewers from './Viewers'
import Movies from './Movies'
import Movie from './Movie'

function Home() {
  const [popular, setPopular] = useState([]);

  const url =
  "https://api.themoviedb.org/3/discover/movie?api_key=12ca358b9af2497731c98a1b7a6caabd&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate";
  useEffect(() => {
    fetchPopular();
  },[]);


  const fetchPopular = async () => {
    const data = await fetch(url);
    const movies = await data.json();
    console.log(movies);
    setPopular(movies.results);
  };

  return (
    <Container>
        <ImgSlider />
        <Viewers />
        <Movies />
      <Content>
        <Wrap>
          {popular.map((movie) => {
          return <Movie key={movie.id} movie={movie} />;
          })}
        </Wrap>
      </Content>
    </Container>
  );
}

export default Home

const Content = styled.div`
    display: grid;
    grid-gap: 25px;
    grid-template-columns: repeat(4, minmax(0, 1fr));
`

const Wrap = styled.div`
    border-radius: 10px;
    cursor: pointer;
    overflow: hidden;
    border: 3px solid rgba(249, 249, 249, 0.1);
    box-shadow: rgba(0 0 0 /69%) 0px 26px 30px -10px,
    rgb(0 0 0 / 73%) 0px 16px 10px -10px;
    transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;

    // &:hover {
    //     transform: scale(1.05);
    //     border-color: rgba(249, 249, 249, 0.8);
    //     box-shadow: rgba(0 0 0 /80%) 0px 40px 58px -16px,
    //     rgb(0 0 0 / 72%) 0px 30px 22px -10px;
    // }

` 

const Container = styled.main`
    min-height: calc(100vh - 70px);
    padding: 0 calc(3.5vw + 5px);
    position: relative;
    overflow-x: hidden;

    &:before {
        background: url("/images/home-background.png") center center / cover;
        no-repeat fixed;
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        z-index: -1;
    }


`
